// Array to store all possible todos status
let todosStatus = [];

// Get todos status from database
const fetchStatus = () => {
  // Code generated by POSTMAN
  var requestOptions = {
    method: "GET",
    redirect: "follow",
  };

  fetch("http://localhost:8001/getStatus.php", requestOptions)
    .then((response) => response.text())
    .then((result) => {
      const allStatus = JSON.parse(result);
      // Clear array
      todosStatus = [];
      // Add each status to the array
      allStatus.forEach((status) => {
        todosStatus.push(status);
      });
    })
    .catch((error) => console.log("error", error));
};

// Call function on page load
fetchStatus();
